import React, { useState, useEffect } from 'react';
import { getIPs, addIPsFromFile, addIPsFromText, validateApiKey } from '../api';
import { 
    TextField, Button, Table, TableBody, TableCell, 
    TableContainer, TableHead, TableRow, Paper, CircularProgress, 
    Alert, Grid, Typography 
} from '@mui/material';

const Dashboard = () => {
    const [ips, setIps] = useState([]);
    const [textInput, setTextInput] = useState('');
    const [fileInput, setFileInput] = useState(null);
    const [apiKey, setApiKey] = useState(localStorage.getItem('apiKey') || '');
    const [isApiKeyValid, setIsApiKeyValid] = useState(!!localStorage.getItem('apiKey'));
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchIps = async () => {
        try {
            const response = await getIPs();
            setIps(response.data);
        } catch (error) {
            console.error("Error fetching IPs:", error);
            setError('Failed to fetch IPs. Please try again later.');
        }
    };

    useEffect(() => {
        fetchIps();
    }, []);

    const handleApiKeyChange = (e) => {
        setApiKey(e.target.value);
    };

    const handleApiKeyValidate = async () => {
        if (!apiKey) {
            setError('API key is required.');
            return;
        }
        setLoading(true);
        try {
            const response = await validateApiKey(apiKey);
            if (response.data.is_valid) {
                localStorage.setItem('apiKey', apiKey);
                setIsApiKeyValid(true);
                setError('');
            } else {
                localStorage.removeItem('apiKey');
                setIsApiKeyValid(false);
                setError('Invalid API key.');
            }
        } catch (error) {
            console.error("Error validating API key:", error);
            setError('Failed to validate API key. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleTextSubmit = async () => {
        if (!textInput.trim()) return;
        setLoading(true);
        try {
            await addIPsFromText(textInput, apiKey);
            setTextInput('');
            fetchIps();
        } catch (error) {
            console.error("Error submitting text IPs:", error);
            setError('Failed to submit IPs from text. Please check your input and API key.');
        } finally {
            setLoading(false);
        }
    };

    const handleFileChange = (e) => {
        setFileInput(e.target.files[0]);
    };

    const handleFileSubmit = async () => {
        if (!fileInput) return;
        setLoading(true);
        try {
            await addIPsFromFile(fileInput, apiKey);
            setFileInput(null);
            document.getElementById('file-input').value = null;
            fetchIps();
        } catch (error) {
            console.error("Error submitting file:", error);
            setError('Failed to submit IPs from file. Please check your file and API key.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Grid container spacing={3}>
            <Grid item xs={12}>
                <Paper sx={{ p: 2 }}>
                    <Typography variant="h6">API Key</Typography>
                    <TextField
                        label="Shodan API Key"
                        value={apiKey}
                        onChange={handleApiKeyChange}
                        fullWidth
                        disabled={isApiKeyValid}
                        sx={{ mt: 1 }}
                    />
                    <Button 
                        variant="contained" 
                        onClick={handleApiKeyValidate} 
                        disabled={loading || isApiKeyValid} 
                        sx={{ mt: 1 }}
                    >
                        {loading ? <CircularProgress size={24} /> : 'Validate Key'}
                    </Button>
                    {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
                </Paper>
            </Grid>
            {isApiKeyValid && (
                <>
                    <Grid item xs={12} md={6}>
                        <Paper sx={{ p: 2 }}>
                            <Typography variant="h6">Add IPs from Text</Typography>
                            <TextField
                                label="Enter one IP per line"
                                multiline
                                rows={4}
                                value={textInput}
                                onChange={(e) => setTextInput(e.target.value)}
                                fullWidth
                                sx={{ mt: 1 }}
                            />
                            <Button 
                                variant="contained" 
                                onClick={handleTextSubmit} 
                                disabled={loading || !textInput.trim()} 
                                sx={{ mt: 1 }}
                            >
                                {loading ? <CircularProgress size={24} /> : 'Submit IPs'}
                            </Button>
                        </Paper>
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <Paper sx={{ p: 2 }}>
                            <Typography variant="h6">Add IPs from File</Typography>
                            <input type="file" id="file-input" onChange={handleFileChange} />
                            <Button 
                                variant="contained" 
                                onClick={handleFileSubmit} 
                                disabled={loading || !fileInput} 
                                sx={{ mt: 1 }}
                            >
                                {loading ? <CircularProgress size={24} /> : 'Upload File'}
                            </Button>
                        </Paper>
                    </Grid>
                </>
            )}
            <Grid item xs={12}>
                <TableContainer component={Paper} sx={{ mt: 3 }}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>IP Address</TableCell>
                                <TableCell>Country</TableCell>
                                <TableCell>City</TableCell>
                                <TableCell>Organization</TableCell>
                                <TableCell>OS</TableCell>
                                <TableCell>Open Ports</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {ips.map(ip => (
                                <TableRow key={ip.id}>
                                    <TableCell>{ip.ip_address}</TableCell>
                                    <TableCell>{ip.country}</TableCell>
                                    <TableCell>{ip.city}</TableCell>
                                    <TableCell>{ip.org}</TableCell>
                                    <TableCell>{ip.os}</TableCell>
                                    <TableCell>{ip.ports.join(', ')}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Grid>
        </Grid>
    );
};

export default Dashboard;
